SELECT CONCAT(emp.FIRST_NAME, ' ', emp.LAST_NAME) AS 'Nome completo', jobs.JOB_TITLE AS 'Cargo', job_history.START_DATE AS 'Data de in√≠cio do cargo', dep.DEPARTMENT_NAME AS 'Departamento' FROM hr.employees as emp INNER JOIN hr.job_history as job_history ON emp.EMPLOYEE_ID = job_history.EMPLOYEE_ID INNER JOIN hr.jobs AS jobs ON jobs.JOB_ID = job_history.JOB_ID INNER JOIN hr.departments AS dep ON dep.DEPARTMENT_ID = job_history.DEPARTMENT_ID ORDER BY `Nome Completo` DESC, `Cargo`;